import React, { useState } from "react";

export default function Modal(props) {
  const [showModal, setShowModal] = useState(false);
  return (
    // only show modal if showModal === true
    <div className="modal" style={{ display: showModal ? flex : none }}>
      {props.children}
    </div>
  );
}
