import React, {useEffect} from "react";
import "./modal.scss";
export default function Modal({
  showModal,
  setShowModal,
  handleOnClose,
  children,
}) {
  useEffect(()=> {
    if (!showModal) {

      handleOnClose()
    }
  },[showModal])
  return (
    // only show modal if showModal === true
    <>
      {showModal && (
        <>
          <div
            className="backdrop"
            onClick={() => {
              // if (handleOnClose) {
              //   handleOnClose();
              // }
              setShowModal(false);
            }}
          ></div>
          <div className="modal">{children}</div>
        </>
      )}
    </>
  );
}
