import React from "react";
import useDropdown from "./UseDropdown";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronUp, faChevronDown } from "@fortawesome/free-solid-svg-icons";
export default function Dropdown({
  title = "placeholder",
  list = ["1", "2", "3"],
}) {
  const { listOpen, toggleList } = useDropdown();
  const ListItem = ({ item }) => <li className="dd-list-item">{item}</li>;
  const listItems = list.map((item) => <ListItem item={item} />);
  return (
    <div className="dd-backdrop">
      <div className="dd-wrapper">
        <div className="dd-header">
          <button type="button" className="dd-header" onClick={toggleList}>
            <div className="dd-header-title">{title}</div>
          </button>
        </div>
        <ul className="dd-list">{listItems}</ul>
      </div>
    </div>
  );
}
