import { useState } from "react";
import { convertTime } from "../../../../utils/convertTime";
export default function useCreateModal(initialState, addTask) {
  const [form, setForm] = useState(initialState);
  const [error, setError] = useState({});

  function validateOnChange(name, newValue, oldState) {
    const validDay = /^([0-9]|[1-8][0-9]|9[0-9]|[12][0-9]{2}|3[0-5][0-9]|36[0-3])$/;
    const validHour = /^([0-9]|1[0-9]|2[0-3])$/;
    switch (name) {
      case "startDay":
        if (newValue === "") {
          return newValue;
        }
        // check if valid day 0 - 363
        if (validDay.test(newValue)) {
          return newValue;
        }
        return oldState[name];
      case "startHour":
        if (newValue === "") {
          return newValue;
        }
        if (validHour.test(newValue)) {
          if (newValue < oldState.endHour || !oldState.endHour) {
            return newValue;
          }
        }
        return oldState[name];
      case "endHour":
        if (newValue === "") {
          return newValue;
        }
        if (validHour.test(newValue)) {
          if (newValue > oldState.startHour || !oldState.startHour) {
            return newValue;
          }
        }
        return oldState[name];
      default:
        return newValue;
    }
  }
  function handleOnChange(e) {
    const { name, value } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: validateOnChange(name, value, prev),
    }));
  }
  function handleDriverChange(driver) {
    setForm((prev) => ({ ...prev, driver }));
  }
  function handleTaskTypeChange(taskType) {
    setForm((prev) => ({ ...prev, taskType }));
  }

  function handleAddTask() {
    // need to transform data so form state because task state in useCalendar
    const {
      name,
      description,
      taskType,
      driver,
      startLocation,
      endLocation,
      startDay,
      startHour,
      endDay,
      endHour,
    } = form;
    const startTime = convertTime(startDay, "day", "hour") + startHour;
    const endTime = convertTime(endDay, "day", "hour") + endHour;
    const task = {
      name: name,
      detail: description,
      type: taskType.id,
      driver,
      location: { start: startLocation, end: endLocation },
      time: { start: startTime, end: endTime },
    };
    addTask(task);
  }
  return {
    form,
    handleOnChange,
    handleDriverChange,
    handleTaskTypeChange,
    handleAddTask,
  };
}
