import React from "react";
import "./modal.scss";
export default function Modal({
  showModal,
  setShowModal,
  handleOnClose,
  children,
}) {
  return (
    // only show modal if showModal === true
    <>
      {showModal && (
        <>
          <div
            className="backdrop"
            onClick={() => {
              if (handleOnClose) {
                handleOnClose();
              }
              setShowModal(false);
            }}
          ></div>
          <div className="modal">{children}</div>
        </>
      )}
    </>
  );
}
