import React, { useState } from "react";
import "*./modal.scss"
export default function Modal(props) {

  return (
    // only show modal if showModal === true
    <div className="modal" style={{ display: props.showModal ? 'flex' : 'none' }}>
      {props.children}
    </div>
  );
}
