import { useState } from "react";
export default function useGenerateCsf(weeklyTasks, driversList) {
  const [csvInterval, setCsvInterval] = useState({ id: 2, name: "2 days" });
  const [driverSelected, setDriverSelected] = useState(driversList[0]);
  const possibleInterval = [
    { id: 2, name: "2 days" },
    { id: 4, name: "4 days" },
    { id: 7, name: "7 days" },
    { id: 14, name: "14 days" },
    { id: 28, name: "28 days" },
  ];
  function summarizeDriverActivities() {}
  return {
    csvInterval,
    setCsvInterval,
    possibleInterval,
    driverSelected,
    setDriverSelected,
  };
}
