import React from "react";
import Modal from "../../../../Modal/Modal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faMapMarkerAlt,
  faClock,
  faTruckMoving,
} from "@fortawesome/free-solid-svg-icons";
import "./taskModal.scss";
export default function TaskModal({ taskInfo, showModal, setShowModal }) {
  return (
    <Modal showModal={showModal} setShowModal={setShowModal}>
      <div className="task-wrapper">
        <div className="task-line">
          {/* choose task name */}
          <div>{taskInfo.name}</div>

          {/* choose task type */}
          <div>{taskInfo.type}</div>
        </div>
        <div className="task-line">
          <FontAwesomeIcon icon={faTruckMoving} />

          <div>{taskInfo.driver.name}</div>
        </div>

        <div className="task-line">
          <FontAwesomeIcon icon={faClock} />

          <div>{taskInfo.time.start}</div>
          <span>-</span>
          <div>{taskInfo.time.end}</div>
        </div>

        <div className="task-line">
          <FontAwesomeIcon icon={faMapMarkerAlt} />

          <div>from {taskInfo.location.start}</div>
          <div>to {taskInfo.location.finish}</div>
        </div>
        <div className="task-line">
          <div>{taskInfo.detail}</div>
        </div>
      </div>
    </Modal>
  );
}
