import React from "react";
import Modal from "../../../../Modal/Modal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faMapMarkerAlt,
  faClock,
  faTruckMoving,
} from "@fortawesome/free-solid-svg-icons";
import "./taskModal.scss";
export default function TaskModal({ taskInfo, showModal, setShowModal }) {
  return (
    <Modal showModal={showModal} setShowModal={setShowModal}>
      <div className="create-modal-content-wrapper">
        <div className="create-modal-first">
          <div className="task-name-input-wrapper">
            {/* choose task name */}
            <div>{taskInfo.name}</div>
          </div>
          <div className="task-type-dd-wrapper">
            {/* choose task type */}
            <div>{taskInfo.type}</div>
          </div>
        </div>
        <div className="create-modal-icon twos">
          <FontAwesomeIcon icon={faTruckMoving} />

          <div>{taskInfo.driver.name}</div>
        </div>

        <div className="create-modal-icon fours">
          <FontAwesomeIcon icon={faClock} />

          <div>{taskInfo.time}</div>
          <span>-</span>
        </div>

        <div className="create-modal-icon twos">
          <FontAwesomeIcon icon={faMapMarkerAlt} />

          <div>from {taskInfo.location.start}</div>
        </div>
        <div className="create-modal-first description">
          <div>{taskInfo.detail}</div>
        </div>
      </div>
    </Modal>
  );
}
